        {choice === "" ? (
          <div className={styles.btns}>
            <button onClick={() => setChoice("links")}>Links</button>
            <button onClick={() => setChoice("todos")}>Todos</button>
          </div>
        ) : (
          handleChoice(choice)
        )}
      </div>
    </main>
  );
}

export default Main;
