import { useState, useEffect } from "react";
import Login from "./Login.js";
import Home from "./Home.js";
function App() {
  const [loggedIn, setLoggedIn] = useState(false);

  useEffect(() => {
    const name = localStorage.getItem("username");
    if (name === null) {
      name = "";
    } else {
      setLoggedIn(true);
    }
  }, []);

  return (
    <div>{loggedIn ? <Home /> : <Login />}</div>
  );
}
export default App;
